#!/usr/bin/env bash
rm -f temp_.sqlite3

# INTEGER
# Для sqlite не имеет значение параметр после типа (INTEGER(4)).
echo -e "\e[92mINTEGER\e[0m"
sqlite3 temp_.sqlite3 <<-SQL
  DROP TABLE IF EXISTS a;
  CREATE TABLE a (i INTEGER);
  INSERT INTO a VALUES ();
  SELECT i, typeof(i) FROM a;

  DROP TABLE IF EXISTS a;
  CREATE TABLE a (i INTEGER(4));
  INSERT INTO a VALUES (999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999999);
  INSERT INTO a VALUES (9223372036854775807);
  INSERT INTO a VALUES (-9223372036854775808);
  SELECT i, typeof(i) FROM a;
SQL
